declare namespace Ctx {
  type Pages = {
    path?: string;
    style?: {
      [key: string]: any;
    };
    [key: string]: any;
  }[];

  type SubPackages = {
    root?: string;
    pages?: Ctx.Pages;
    [key: string]: any;
  }[];

  interface Data {
    appid?: string;
    pages?: Ctx.Pages;
    subPackages?: Ctx.SubPackages;
    modules?: string[];
    [key: string]: any;
  }
}

declare namespace Config {
  interface Options {
    // 代理配置
    proxy?: any;
    // 是否为插件
    isPlugin?: boolean;
    // tailwind
    tailwind?: {
      // 是否开启tailwind
      enable?: boolean;
      // 根元素字体大小
      remUnit?: number;
      // 小数位数
      remPrecision?: number;
      // 转换比例
      rpxRatio?: number;
      // 暗黑模式文本类名
      darkTextClass?: string;
    };
  }
}
