
//#region src/volar-plugin.ts
const nativeTags = [
	"block",
	"component",
	"template",
	"slot"
];
var volar_plugin_default = () => ({
	version: 2,
	resolveTemplateCompilerOptions(options) {
		options.isNativeTag = (tag) => nativeTags.includes(tag);
		return options;
	}
});

//#endregion
module.exports = volar_plugin_default;